sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

    // get value of what needed, this is needed to set (line# 34) its value from DB for form loading case
	var whatNeeded = g_form.getValue('u_what_needed');

	// clear all values of what needed
	g_form.clearOptions('u_what_needed');

	// add options for what needed based on requested for newValue
	if (newValue == 'hr') {
		g_form.addOption('u_what_needed', 'hr1', 'Humar Resources 1');
		g_form.addOption('u_what_needed', 'hr2', 'Humar Resources 2');
		g_form.addOption('u_what_needed', 'other', 'Other');

	} else if (newValue == 'facilities') {
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'other', 'Other');

	} else if (newValue == 'legal') {
		g_form.addOption('u_what_needed', 'legal2', 'Legal 1');
		g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
		g_form.addOption('u_what_needed', 'other', 'Other');

	}

	// if the form is loading and is not new record put the value from the DB teh previously saved ones
	// this prevents in overwriting the field value while form is loading 

	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatNeeded);
	}


}
