sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
      return;
   }

    // create instance of the GetEmailAddress Script Include
    var getEmailAddress = new GlideAjax('GetEmailAddress');
    // specify the method name (via sysparm_name) in the Script Include
	getEmailAddress.addParam('sysparm_name', 'getEmail');
	// pass on the sysparm_userID to script Include
	getEmailAddress.addParam('sysparm_userID', newValue);
	// invoke the Script Include passing the callback as argument
	getEmailAddress.getXML(populateEmail);

	// define the populateEmail callback
	function populateEmail(response) {
		// get the email from the response
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		// clear the value (if any) in the Requested for email field
		g_form.clearValue('u_requested_for_email');
		// set the value retrieved from Script Include
		g_form.setValue('u_requested_for_email', emailFromScriptInclude);
	}

}
