sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail: function() {
		// get user record from sys_user
		var users = new GlideRecord("sys_user");
		// get user sys_id passed by client
		var sys_id = this.getParameter('sysparm_userID');
		// Note: The syntax sysparm_ indicates to the Script Include that a value will be passed in from a client-side script at runtime

		// get user by using sys_id
		users.get(sys_id);
		return users.email + '';
	},

    type: 'GetEmailAddress'
});
